let search ="agents";
let happened = false;

function Agents() {
    fullData();
    //find();

    return <div id="Agents">
        <img src={"https://media.valorant-api.com/playercards/9fb348bc-41a0-91ad-8a3e-818035c4e561/largeart.png"} className="App-logo" alt="logo" id="b"/>
        <h2 id={"a"}></h2>

        <select id="Agents">

        </select>
        <button id="button" onClick={change}>Change</button>
    </div>

}

async function fullData() {
    const response = await fetch(url + search + "?isPlayableCharacter=true");
    const data = await response.json();
    let agentOption = "<option value'0'>s</option>";
    if (!happened){

        for (let i = 0; i < data.data.length; i++){
            console.log(data.data[i].displayName);

            agentOption+= "<option value'"+data.data[i].uuid+"'>"+data.data[i].displayName+"</option>";

        }
        happened = true;
        document.getElementById('Agents').innerHTML = agentOption;
    }

    else {
        happened = false;
    }


    //console.log()
    //console.log(data.data[0].displayName)
}

async function find() {

    const response = await fetch(url +search + "/"+ uuid);
    const data = await response.json();
    console.log("Vastaus apilta");
    console.log(data.data.displayName);
    //console.log(data.);
    /*
        document.getElementById('a').textContent =data.data.displayName ;
        document.getElementById('b').src=data.data.largeArt;
        */
}

function change(){
    uuid = (document.getElementById("Agents").value);
    find();
}

export default Agents;
